import React, {useState}  from 'react';
import axios from 'axios';
import './Login.css';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

class Profile extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        email: props.email,
        userEmail: "",
        userPassword: "",
        userName: "",
        userAvatar: ""
      };
    }

    componentDidMount() {
      axios({method: 'get',
      url: `https://localhost:44327/api/Account/Profile/?email=${this.state.email}`,
      headers: {'Content-Type': 'application/json'}})
        .then(
          (result) => {
            console.log(result)
            this.setState({
              isLoaded: true,
              userEmail: result.data.Email,
            userPassword: "",
            userName: result.data.Name,
            userAvatar: ""
              
            });
            console.log(this.state.userEmail)
          },

          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
        console.log(this.state.userEmail)
    }
    /*.map(item => (
          <li key={item.Id}>
             {item.Id}
             {item.Name}
             
          </li>
        ))}*/
  
    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Ошибка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Загрузка...</div>;
      } else {
        return (
            
        <ul>
        <li>
        {this.state.userEmail}</li>
        <li> 
            {this.state.userName}
        </li>
        </ul>
        )
      }
    }
  }
  
export default  Profile
