import React, {useState}  from 'react';
    import axios from 'axios';
    import './Courses.css';
    import Form from "react-bootstrap/Form";
    import Button from "react-bootstrap/Button";

export default function Corse(props){

        const [id, setId] = useState("");
        const [name, setName] = useState("");
        const [desc, setDesc] = useState("");
        const [author, setAuthor] = useState("");
        const [tests, setTests] = useState([]);
    
        function GetCourses() {
            axios({method: 'get',
            url: `https://localhost:44327/api/Course/?id=${props.courseid}`,
            headers: {'Content-Type': 'application/json'}})
              .then(
                (result) => {
                  console.log(result.data)
                  setName(result.data.CourseName)
                  setDesc(result.data.CourseDescription)
                  setTests(result.data.Tests);
                }
              )
          }
           
          GetCourses();
    
          const Acha = tests.map((item => <ul key = {item.Id}><li>{item.CourseName}</li><li>{item.CourseDescription}</li></ul>));
          console.log({Acha});
    
          return(
            <div>
            {Acha}
            </div>
          )
    
}
