import React, {useState, useEffect}  from 'react';
    import axios from 'axios';
    import './Courses.css';
    import Form from "react-bootstrap/Form";
    import Button from "react-bootstrap/Button";
import { useAppContext } from '../Libs/ContextLib';

export default function Course(props){

        const [course, setCourse] = useState();  
        const [tests, setTests] = useState(); 
        const [isLoading, setIsLoading] = useState(true);
        const [error, setError] = useState();
        
        function GetCourse() {
            axios({method: 'get',
            url: `https://localhost:44327/api/Course/?id=${props.match.params.id}`,
            headers: {'Content-Type': 'application/json'}})
              .then(
                (result) => {
                  console.log(result.data)
                  setCourse(result.data)
                  setIsLoading(false);
                }
              ).catch(error => {setError(error);
                                setIsLoading(false);}) 
          }

          function GetTests() {
            axios({method: 'get',
            url: `https://localhost:44327/api/Test/?id=${props.match.params.id}`,
            headers: {'Content-Type': 'application/json'}})
              .then(
                (result) => {
                  console.log(result.data)
                  setTests(result.data)
                  setIsLoading(false);
                }
              ).catch(error => {setError(error);
                                setIsLoading(false);}) 
          }

        useEffect(() => {
             GetCourse();
             GetTests()
        }, []);

       
        
        if(isLoading){
            return(
            <p>Loading...</p>
            )
        }
        else if(error){
            return(<p>Error</p>)
        }

        

        const Tests = tests.map((item => <ul key = {item.Id}><li>{item.CourseName}</li><li>{item.CourseDescription}</li></ul>));
        console.log({Tests});

        return(
        <div>
            <h3>{course.CourseName}</h3>
            <h6>{course.CourseDescription}</h6>
        {Tests}
        </div>
        )
    
}
