import React, {useState}  from 'react';
import axios from 'axios';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import './Register.css';

export default class PostUser extends React.Component {
  state = {
    Email: '',
    Password: '',
    ConfirmPassword: '',
    Name: '',
    isprofessor: false
  }

  handleChange = event => {
    this.setState({ Email: event.target.value });
  }
  handleChange2 = event => {
    this.setState({ Password: event.target.value });
  }
  handleChange3 = event => {
    this.setState({ ConfirmPassword: event.target.value });
  }
  handleChange4 = event => {
    this.setState({ Name: event.target.value });
  }
  handleChange5(event) {
    this.setState({isprofessor: !this.state.isprofessor});
  }
 
  handleSubmit = event => {
    event.preventDefault();
    const user = {
        Email: this.state.Email,
        Password: this.state.Password,
        ConfirmPassword: this.state.ConfirmPassword,
        Name: this.state.Name,
        isprofessor: this.state.isprofessor.toString()
    };
    let str = JSON.stringify(user);
    axios({
      method: 'post',
      url: "https://localhost:44327/api/Account/Register",
      data: JSON.stringify(user),
      maxContentLength: Infinity,
      maxBodyLength: Infinity,
      headers: {'Content-Type': 'application/json'}
  })
      .then(res => {
        console.log("RESPONSE ", res);
        console.log(res.data);
      })
  }

  render() {
    /*return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Person Name:
            <input type="text" name="Email" onChange={this.handleChange} />
            <input type="text" name="Password" onChange={this.handleChange2} />
            <input type="text" name="ConfirmPassword" onChange={this.handleChange3} />
            <input type="text" name="Name" onChange={this.handleChange4} />
            <input type="checkbox" id="contactChoice1"
            name="isprofessor" 
            isprofessor={this.state.isprofessor.toString()}
            onChange={this.handleChange5.bind(this)} />
            <label htmlFor="contactChoice1">Professor</label>
          </label>
          <button type="submit">Add</button>
        </form>
      </div>
    )*/

    return (     
        <div className="Login">
        <Form onSubmit={this.handleSubmit}>
          <Form.Group size="lg" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              autoFocus
              type="email"
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              onChange={this.handleChange2}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              onChange={this.handleChange3}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              onChange={this.handleChange4}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="isprofessor">
            <Form.Label>Professor</Form.Label>
            <Form.Control
              type="checkbox"
              isprofessor={this.state.isprofessor.toString()}
              onChange={this.handleChange5.bind(this)}
            />
          </Form.Group>
          <Button block size="lg" type="submit">
            Login
          </Button>
        </Form>
      </div>
      )

  }
}
