import React, {useState}  from 'react';
import axios from 'axios';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import './Register.css';

export default function PostUser() {
  state = {
    Email: '',
    Password: '',
    ConfirmPassword: '',
    Name: '',
    isprofessor: false
  }

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const [name, setName] = useState("");
  const [isprofessor, setIsProfessor] = useState(false);
  const { userHasAuthenticated } = useAppContext();

 /* handleChange = event => {
    this.setState({ Email: event.target.value });
  }
  handleChange2 = event => {
    this.setState({ Password: event.target.value });
  }
  handleChange3 = event => {
    this.setState({ ConfirmPassword: event.target.value });
  }
  handleChange4 = event => {
    this.setState({ Name: event.target.value });
  }
  handleChange5 = event => {
    this.setState({isprofessor: !this.state.isprofessor});
  }*/
 
  function handleSubmit (event) {
    event.preventDefault();
    const user = {
        Email: this.state.Email,
        Password: this.state.Password,
        ConfirmPassword: this.state.ConfirmPassword,
        Name: this.state.Name,
        isprofessor: this.state.isprofessor.toString()
    };
    JSON.stringify(user);
    axios({
      method: 'post',
      url: "https://localhost:44327/api/Account/Register",
      data: JSON.stringify(user),
      maxContentLength: Infinity,
      maxBodyLength: Infinity,
      headers: {'Content-Type': 'application/json'}
  })
      .then(res => {
        console.log("RESPONSE ", res);
        console.log(res.data);
      })
  }

    /*return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Person Name:
            <input type="text" name="Email" onChange={this.handleChange} />
            <input type="text" name="Password" onChange={this.handleChange2} />
            <input type="text" name="ConfirmPassword" onChange={this.handleChange3} />
            <input type="text" name="Name" onChange={this.handleChange4} />
            <input type="checkbox" id="contactChoice1"
            name="isprofessor" 
            isprofessor={this.state.isprofessor.toString()}
            onChange={this.handleChange5.bind(this)} />
            <label htmlFor="contactChoice1">Professor</label>
          </label>
          <button type="submit">Add</button>
        </form>
      </div>
    )*/

    return (     
        <div className="Login">
        <Form onSubmit={handleSubmit}>
          <Form.Group size="lg" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              autoFocus
              type="email"
              value={email}
            onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
            onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              value={confPassword}
            onChange={(e) => setConfPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
            onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Check size="lg" controlId="isprofessor" style={{display: 'flex',
                                                        flexFlow: 'row',
                                                        justifyContent: 'left',}}>
            <Form.Check
              type="checkbox"
              class="form-check-input"
              value={isprofessor.toString()}
              onChange={(e) => setIsProfessor(e.target.value)}
              /*isprofessor={state.isprofessor.toString()}
              onChange={this.handleChange5.bind(this)}*/
            />
            <Form.Label>Professor</Form.Label>
          </Form.Check>
          <Button block size="lg" type="submit">
            Sign Up
          </Button>
        </Form>
      </div>
      )
 }
