import React, {useState, useEffect}  from 'react';
    import axios from 'axios';
    import './Courses.css';
    import Form from "react-bootstrap/Form";
    import Button from "react-bootstrap/Button";
import { useAppContext } from '../Libs/ContextLib';

export default function Course(props){

        const [course, setCourse] = useState();  
        const [name, setName] = useState(); 
        const [desc, setDesc] = useState(); 
        const [isLoading, setIsLoading] = useState(true);
        const [error, setError] = useState();
        
        function GetCourse() {
            axios({method: 'get',
            url: `https://localhost:44327/api/Course/?id=${props.match.params.id}`,
            headers: {'Content-Type': 'application/json'}})
              .then(
                (result) => {
                  console.log(result.data)
                  setCourse(result.data)
                  setIsLoading(false);
                }
              ).catch(error => {setError(error);
                                setIsLoading(false);}) 
          }

        useEffect(() => {
             GetCourse();
        }, []);

        if(isLoading){
            return(
            <p>Loading...</p>
            )
        }
        else if(error){
            return(<p>Error</p>)
        }
        
        

        const Tests = course.Tests.map((item => <ul key = {item.Id}><li>{item.TestName}</li><li>{item.TestDescription}</li></ul>));
        console.log({Tests});

        return(
        <div>
            <Form.Group size="lg" controlId="name">
        <Form.Label><h3>Course</h3></Form.Label>
        <Form.Control
            type="text"
            value={course.CourseName}
        onChange={(e) => setName(e.target.value)}
        />
        </Form.Group>
        <Form.Group size="lg" controlId="desc">
        <Form.Label><h6>Description</h6></Form.Label>
        <Form.Control
            type="text"
            value={course.CourseDescription}
        onChange={(e) => setDesc(e.target.value)}
        />
        </Form.Group>
        <br />
        <br />
        <h8>Tests:</h8>
        {Tests}
        </div>
        )
    
}
