import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import './Courses.css';
import Button from "react-bootstrap/Button";
import history from "../GlobalHistory/GlobalHistory"
import Edit from '../Res/Imagesedit.png';

export default function Courses(props){
    const [courses, setCourses] = useState();
    const [user, setUser] = useState();
    const [isLoading, setIsLoading] = useState(true);
        const [error, setError] = useState();

        function GetUser() {
          axios({method: 'get',
          url: `https://localhost:44327/api/Account/Profile/?email=${props.match.params.email}`,
          headers: {'Content-Type': 'application/json'}})
            .then(
              (result) => {
                console.log(result)
                setUser(result.data);
              }
            )
        }

    function GetCourses() {
        axios({method: 'get',
        url: `https://localhost:44327/api/Course/ProfCourses/?email=${props.match.params.email}`,
        headers: {'Content-Type': 'application/json'}})
          .then(
            (result) => {
              console.log(result.data)
              setCourses(result.data)
              setIsLoading(false);
            }
          ).catch(error => {setError(error);
                            setIsLoading(false);}) 
      }
       
      useEffect(() => {
        GetUser();
        GetCourses();
   }, []);

  
   
   if(isLoading){
       return(
       <p>Loading...</p>
       )
   }
   else if(error){
       return(<p>Error</p>)
   }

      //const Acha = courses.map((item => <ul key = {item.Id}><li>{item.CourseName}<LinkContainer to="/course">Edit</LinkContainer></li><li>{item.CourseDescription}</li></ul>));

      return(
        <div className="Course">
          <h1>Courses</h1><div className="mycontainer">
      <p>Prof. {user.Name}</p>
          <Button onClick={() => history.push(`/addcourse/${props.match.params.email}`)}><div className="mybtn">Add a course</div></Button></div>
          {courses.map((item => <div className="coursecontainer" key = {item.Id}><div><img width="600px" src="https://img.jakpost.net/c/2019/03/02/2019_03_02_66706_1551461528._large.jpg"/>
          <Button className="mybutton" onClick={() => history.push(`/course/${item.Id}`)}><img src={edit}/></Button></div><div className="info"><h1>{item.CourseName}
          <Button onClick={() => history.push(`/stats/${item.Id}`)}>Statistics</Button>
          <Button onClick={() => history.push(`/course/${item.Id}`)}>Edit</Button>
          <Button onClick={() => {axios.delete(`https://localhost:44327/api/Course/?id=${item.Id}`)
      .then(res => {
        console.log(res);
        console.log(res.data);
      })}}>Delete</Button></h1><br /><p>Description: {item.CourseDescription}</p><br /><p>Average Mark: {item.AvgMark}</p></div></div>))}
        </div>
      )

}
