import React, {useState}  from 'react';
import axios from 'axios';
import './Login.css';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default function Profile(props){

    const [avatar, setAvatar] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [name, setName] = useState("");
    const [isprofessor, setIsProfessor] = useState(false);

    function GetUser() {
      axios({method: 'get',
      url: `https://localhost:44327/api/Account/Profile/?email=${props.email}`,
      headers: {'Content-Type': 'application/json'}})
        .then(
          (result) => {
            console.log(result)
            setName(result.data.Name)
            setEmail(result.data.Email)
            setAvatar(result.data.UserAvatar)
          }
        )
    }

    function handleSubmit(event) {
        event.preventDefault();
        if(password == null){
            const user = {
                UserAvatar: avatar,
                Email: email,
                Name: name
            };
        }
        else{
            const user = {
                UserAvatar: avatar,
                Email: email,
                Password: password,
                Name: name
            };
        }
        JSON.stringify(user);
        axios({
          method: 'put',
          url: "https://localhost:44327/api/Account/",
          data: JSON.stringify(user),
          maxContentLength: Infinity,
          maxBodyLength: Infinity,
          headers: {'Content-Type': 'application/json'}
        })
          .then(res => {
            console.log("RESPONSE ", res);
            console.log(res.data);
            if(res.data.Succedeed){
              alert("You`ve been succesfully logged in");
            }  
            else alert("Wrong email or password");
          })
          
      }

    /*
    <ul>
            <li>
                <img src={this.state.userAvatar} width="512px" height="512px"/>
            </li>
            <Button>Upload</Button>
            <li> 
                {this.state.userName} <Button>Change</Button>
            </li>
            
            <li>
                {this.state.userEmail}<Button>Change</Button>
            </li>
            
            
            <li> 
                {this.state.password}<Button>Change</Button>
            </li>
            <Button>Save</Button>
        </ul>
    */
  GetUser()
        return (
        <div className="Profile">
        <Form onSubmit={handleSubmit}>
          <Form.Group size="lg" controlId="email">
            <Form.Label>Avatar</Form.Label>
            <img src={avatar} width="512px" height="512px"/>
            <Form.Control
              autoFocus
              type="link"
              value={avatar}
            onChange={(e) => setAvatar(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
            onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              autoFocus
              type="email"
              value={email}
            onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={this.state.userPassword}
            onChange={(e) => setPassword(e.target.value)}
            />
             </Form.Group>
          <Button block size="lg" type="submit">
            Save
          </Button>
        </Form>
      </div>
        )
}
