import React, {useState}  from 'react';
import axios from 'axios';
import './Login.css';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

class Profile extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        email: props.email,
        userEmail: "",
        userPassword: "",
        userName: "",
        userAvatar: ""
      };
    }

    componentDidMount() {
      axios({method: 'get',
      url: `https://localhost:44327/api/Account/Profile/?email=${this.state.email}`,
      headers: {'Content-Type': 'application/json'}})
        .then(
          (result) => {
            console.log(result)
            this.setState({
              isLoaded: true,
              userEmail: result.data.Email,
            userPassword: "",
            userName: result.data.Name,
            userAvatar: ""
              
            });
            console.log(this.state.userEmail)
          },

          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
        console.log(this.state.userEmail)
    }
    /*
    <ul>
            <li>
                <img src={this.state.userAvatar} width="512px" height="512px"/>
            </li>
            <Button>Upload</Button>
            <li> 
                {this.state.userName} <Button>Change</Button>
            </li>
            
            <li>
                {this.state.userEmail}<Button>Change</Button>
            </li>
            
            
            <li> 
                {this.state.password}<Button>Change</Button>
            </li>
            <Button>Save</Button>
        </ul>
    */
  
    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Ошибка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Загрузка...</div>;
      } else {
        return (
            
        
        <div className="Login">
        <Form onSubmit={handleSubmit}>
          <Form.Group size="lg" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              autoFocus
              type="email"
              value={email}
            onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
            onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              value={confPassword}
            onChange={(e) => setConfPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
            onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Check size="lg" controlId="isprofessor" style={{display: 'flex',
                                                        flexFlow: 'row',
                                                        justifyContent: 'left',}}>
            <Form.Check
              type="checkbox"
              className="form-check-input"
              value={!isprofessor}
              isprofessor={isprofessor.toString()}
              checked={isprofessor}
              onChange={(e) => handleCheckboxChange(e)}
              /*isprofessor={state.isprofessor.toString()}
              onChange={this.handleChange5.bind(this)}*/
            />
            <Form.Label>Professor</Form.Label>
          </Form.Check>
          <Button block size="lg" type="submit">
            Sign Up
          </Button>
        </Form>
      </div>
        )
      }
    }
  }
  
export default  Profile
