import React, {useState}  from 'react';
import axios from 'axios';
import './Login.css';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

class Profile extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        email: props.email
      };
    }
  
    componentDidMount() {
      axios.get(`https://localhost:44327/api/Account/Profile/?email=${this.state.email}`)
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result
            });
          },

          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }
  
    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Ошибка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Загрузка...</div>;
      } else {
        return ({items});
      }
    }
  }
  
export default  Profile
