                    QuestionText: 
                };
                JSON.stringify(answerModel);
                axios({
                    method: 'put',
                    url: `https://localhost:44327/api/Answer/?id=${event.Id}`,
                    data: JSON.stringify(answerModel),
                    maxContentLength: Infinity,
                    maxBodyLength: Infinity,
                    headers: {'Content-Type': 'application/json'}
                })
                    .then(res => {
                    console.log("RESPONSE ", res);
                    console.log(res.data);
                    if(res.data.Succedeed){
                        alert("The test was changed succesfully");
                    }  
                    else alert("The test wasn`t changed");
                    }) */
                }


        useEffect(() => {
                GetQuestion();
        }, []);

        if(isLoading){
            return(
            <p>Loading...</p>
            )
        }
        else if(error){
            return(<p>Error</p>)
        }
        
        

       const Answers = question.Answers.map((item =>   <Form key = {item.Id} onSubmit={handleSubmit2}> 
           
            <Form.Check
              type="checkbox" id={`${item.Id}chb`}
              className="form-check-input"
              defaultChecked={item.IsRight}
            />
          <Form.Group size="lg" controlId={`${item.Id}txt`}>
        <Form.Control
            type="text"
            defaultValue={item.AnswerText}
        />
        </Form.Group>
        <Button block size="lg" type="submit" Id={`${item.Id}btn`}>
        Save
        </Button>
    </Form>));
        console.log({Answers});

        return(
        <div className="Question">
            <Form onSubmit={handleSubmit}>
            <Form.Group size="lg" controlId="name">
        <Form.Label><h3>Question</h3></Form.Label>
        <Form.Control
            type="text"
            defaultValue={question.QuestionText}
        onChange={(e) => setName(e.target.value)}
        />
        </Form.Group>
        <Button block size="lg" type="submit">
        Save
        </Button>
        </Form>
        <br />
        <br />
        <h6>Answers:</h6>
        {Answers}
        
        </div>
        )

