import React, {Fragment, useState } from 'react';
import './App.css';
import Routes from "./Routes";
import NavigationBar from "./Components/NavigationBar";
import { AppContext } from "./Libs/ContextLib";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { LinkContainer } from "react-router-bootstrap";



function App() {
  const [isAuthenticating, setIsAuthenticating] = useState(true);
  const [isAuthenticated, userHasAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  function handleLogout() {
    userHasAuthenticated(false);
    setUserEmail("");
  }
/*{userEmail, setUserEmail}*/
  return (
    <>
      <Navbar collapseOnSelect bg="dark" variant="dark" expand="md" className="mb-3">
        <LinkContainer to="/">
          <Navbar.Brand className="font-weight-bold text-muted">
            Scratch
          </Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle />
        <Navbar.Collapse className="justify-content-end">
          <Nav activeKey={window.location.pathname}>
          {isAuthenticated ? (
            <>
            <LinkContainer to="/profile">
            <Nav.Link>Welcome, {userEmail}</Nav.Link>
            </LinkContainer>
            <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
            </>
            ) : (
            <>
                <LinkContainer to="/signup">
                <Nav.Link>Signup</Nav.Link>
                </LinkContainer>
                <LinkContainer to="/login">
                <Nav.Link>Login</Nav.Link>
                </LinkContainer>
            </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      <AppContext.Provider value={{ isAuthenticated, userHasAuthenticated,  userEmail, setUserEmail}}> 
      <Routes email={userEmail} />
      </AppContext.Provider>
    </>
  );
}

export default App;
