import React, {useState, useEffect}  from 'react';
    import axios from 'axios';
    import './Question.css';
    import Form from "react-bootstrap/Form";
    import Button from "react-bootstrap/Button";

    export default function Question(props){
        const [question, setQuestion] = useState();  
        const [answer, setAnswer] = useState(); 
        const [name, setName] = useState(); 
        const [isLoading, setIsLoading] = useState(true);
        const [error, setError] = useState();

        function GetQuestion() {
            axios({method: 'get',
            url: `https://localhost:44327/api/Question/?id=${props.match.params.id}`,
            headers: {'Content-Type': 'application/json'}})
                .then(
                (result) => {
                    console.log("RESULT: ", result.data)
                    setQuestion(result.data)
                    setIsLoading(false);
                }
                ).catch(error => {setError(error);
                                setIsLoading(false);}) 
            }

            function handleSubmit(event) {
            event.preventDefault();
            const questionModel = {
                QuestionText: name
            };
            JSON.stringify(questionModel);
            axios({
                method: 'put',
                url: `https://localhost:44327/api/Question/?id=${props.match.params.id}`,
                data: JSON.stringify(questionModel),
                maxContentLength: Infinity,
                maxBodyLength: Infinity,
                headers: {'Content-Type': 'application/json'}
            })
                .then(res => {
                console.log("RESPONSE ", res);
                console.log(res.data);
                if(res.data.Succedeed){
                    alert("The test was changed succesfully");
                }  
                else alert("The test wasn`t changed");
                }) 
            }

            function handleSubmit2(event) {
                event.preventDefault();
                const questionModel = {
                    QuestionText: name
                };
                JSON.stringify(questionModel);
                axios({
                    method: 'put',
                    url: `https://localhost:44327/api/Answer/?id=${event.Id}`,
                    data: JSON.stringify(questionModel),
                    maxContentLength: Infinity,
                    maxBodyLength: Infinity,
                    headers: {'Content-Type': 'application/json'}
                })
                    .then(res => {
                    console.log("RESPONSE ", res);
                    console.log(res.data);
                    if(res.data.Succedeed){
                        alert("The test was changed succesfully");
                    }  
                    else alert("The test wasn`t changed");
                    }) 
                }

                function handleCheckboxChange(event){
                    //setIsRight(!);
                  }


        useEffect(() => {
                GetQuestion();
        }, []);

        if(isLoading){
            return(
            <p>Loading...</p>
            )
        }
        else if(error){
            return(<p>Error</p>)
        }
        
        

       const Answers = question.Answers.map((item =>   <div key = {item.Id}>
            <Form.Group size="lg" controlId={item.Id}>
        <Form.Control
            type="text"
            value={item.AnswerText}
        onChange={(e) => setName(e.target.value)}
        />
        </Form.Group>
        <Form.Group size="lg" controlId={item.Id} >
            <Form.Check
              type="checkbox"
              className="form-check-input"
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
              /*isprofessor={state.isprofessor.toString()}
              onChange={this.handleChange5.bind(this)}*/
            />
          </Form.Group>
    </div>));
        console.log({Answers});

        return(
        <div className="Question">
            <Form onSubmit={handleSubmit}>
            <Form.Group size="lg" controlId="name">
        <Form.Label><h3>Question</h3></Form.Label>
        <Form.Control
            type="text"
            value={question.QuestionText}
        onChange={(e) => setName(e.target.value)}
        />
        </Form.Group>
        
        <br />
        <br />
        <h6>Answers:</h6>
        {Answers}
        <Button block size="lg" type="submit">
        Save
        </Button>
        </Form>
        </div>
        )

        /* */
    
}
