import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import './Courses.css';
import Button from "react-bootstrap/Button";
import history from "../GlobalHistory/GlobalHistory"
import Select from 'react-select';
import Sel from '@material-ui/core/Select';

export default function AddCourse(props){
    
    const [name, setName] = useState("");
    const [desc, setDesc] = useState("");
    const [abilities, setAbilities] = useState("");
    const [abs, setAbs] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState();
    const [names, setNames] = useState();

    function Add(event){
        event.preventDefault();
        const course = {
            CourseName: name,
            CourseDescription: desc,
            AuthorEmail: props.match.params.email,
            Abilities: JSON.stringify(abilities)
        };
        axios({
        method: 'post',
        url: "https://localhost:44327/api/Course/",
        data: JSON.stringify(course),
        maxContentLength: Infinity,
        maxBodyLength: Infinity,
        headers: {'Content-Type': 'application/json'}
        })
        .then(res => {
            console.log("RESPONSE ", res);
            console.log(res.data);
        })
    }

    function GetTest() {
      axios({method: 'get',
      url: `https://localhost:44327/api/Ability/Abs/`,
      headers: {'Content-Type': 'application/json'}})
        .then(
          (result) => {
            console.log("RESULT: ", result.data)
            setAbs(result.data)
            setIsLoading(false)
          }
        ).catch(error => {setError(error);
          debugger
                          setIsLoading(false);}) 
        
    }

  useEffect(() => {
       GetTest();
  }, []);

  if(isLoading){
      return(
      <p>Loading...</p>
      )
  }
  else if(error){
      return(<p>Error</p>)
  } 

  
  //const temp = abs.map((item) =>  item.AbilityName);
           /* setNames(temp);*/
            /*console.log("ABS: ", names);*/


            /* <Select
            closeMenuOnSelect={false}
            components={animatedComponents}
            defaultValue={[temp[0]]}
            isMulti
            options={temp}
            />*/
    /*const felix = abs.map((item) => <div key={item.Id}>{item.AbilityName}</div>)
    const fel = JSON.parse(abs);*/
    const fel = abs.map((item) => item);
    return(
       
        <div className="Course">
          <div>
        <form onSubmit={Add}>
          <label>
            Course Name:</label>
            <input type="text" name="Email" onChange={(e) => setName(e.target.value)} />
            <label>Course Description:</label>
            <input type="text" name="Password" onChange={(e) => setDesc(e.target.value)} />
            <label>Distance</label>
            
            <Select
            isMulti
            name="colors"
            options={fel}
            className="basic-multi-select"
            classNamePrefix="select"
            >
          </Select>
          <Button type="submit">Add</Button>
        </form>
      </div>
    <h1>Where is fesfsdgfsgrsgdsrgrgh: {abs}{fel}</h1>
        </div>
      )
}
